"use client";
import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import { authStorage } from "@/lib/auth";
import { menuApi } from "@/lib/api";
import Link from "next/link";

interface MenuItem {
  _id: string;
  name: string;
  description: string;
  price: number;
  category: string;
  isAvailable: boolean;
  isVegetarian?: boolean;
  isGlutenFree?: boolean;
  isKids?: boolean;
}

export default function AdminDashboard() {
  const router = useRouter();
  const [menuItems, setMenuItems] = useState<MenuItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    const token = authStorage.getToken();
    if (!token) {
      router.push("/admin/login");
      return;
    }
    setToken(token);
    fetchMenuItems(token);
  }, [router]);

  const fetchMenuItems = async (authToken: string) => {
    try {
      setLoading(true);
      const response = await menuApi.getAll();
      if (response.success && response.data) {
        setMenuItems(response.data as MenuItem[]);
      }
    } catch (err) {
      console.error("Failed to fetch menu items:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id: string) => {
    if (!token || !confirm("Are you sure you want to delete this item?")) return;

    try {
      const response = await menuApi.delete(id, token);
      if (response.success) {
        fetchMenuItems(token);
      } else {
        alert(response.error || "Failed to delete item");
      }
    } catch (err) {
      alert("Failed to delete item");
    }
  };

  const handleLogout = () => {
    authStorage.removeToken();
    router.push("/admin/login");
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-diner-cream">
        <p className="text-diner-coffee">Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-diner-cream py-12 px-4">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-bold text-diner-coffee">Admin Dashboard</h1>
          <div className="flex gap-4">
            <Link
              href="/admin/orders"
              className="px-4 py-2 border border-diner-coffee text-diner-coffee rounded-lg hover:bg-diner-coffee hover:text-diner-cream transition-colors"
            >
              Manage Orders
            </Link>
            <Link
              href="/admin/menu/new"
              className="px-4 py-2 bg-diner-coffee text-diner-cream rounded-lg hover:bg-diner-terracotta transition-colors"
            >
              Add Menu Item
            </Link>
            <button
              onClick={handleLogout}
              className="px-4 py-2 border border-diner-coffee text-diner-coffee rounded-lg hover:bg-diner-coffee hover:text-diner-cream transition-colors"
            >
              Logout
            </button>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-diner p-6">
          <h2 className="text-2xl font-semibold text-diner-coffee mb-4">Menu Items</h2>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-diner-coffee/20">
                  <th className="text-left py-3 px-4 text-diner-coffee font-semibold">Name</th>
                  <th className="text-left py-3 px-4 text-diner-coffee font-semibold">Category</th>
                  <th className="text-left py-3 px-4 text-diner-coffee font-semibold">Price</th>
                  <th className="text-left py-3 px-4 text-diner-coffee font-semibold">Status</th>
                  <th className="text-left py-3 px-4 text-diner-coffee font-semibold">Actions</th>
                </tr>
              </thead>
              <tbody>
                {menuItems.map((item) => (
                  <motion.tr
                    key={item._id}
                    className="border-b border-diner-coffee/10 hover:bg-diner-cream/50"
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                  >
                    <td className="py-3 px-4">
                      <div className="font-medium text-diner-coffee">{item.name}</div>
                      <div className="text-sm text-diner-coffee/70">{item.description}</div>
                    </td>
                    <td className="py-3 px-4 text-diner-coffee">{item.category}</td>
                    <td className="py-3 px-4 text-diner-gold font-semibold">${item.price.toFixed(2)}</td>
                    <td className="py-3 px-4">
                      <span
                        className={`px-2 py-1 rounded text-xs ${
                          item.isAvailable
                            ? "bg-green-100 text-green-700"
                            : "bg-red-100 text-red-700"
                        }`}
                      >
                        {item.isAvailable ? "Available" : "Unavailable"}
                      </span>
                    </td>
                    <td className="py-3 px-4">
                      <div className="flex gap-2">
                        <Link
                          href={`/admin/menu/${item._id}/edit`}
                          className="text-diner-terracotta hover:text-diner-coffee text-sm font-medium"
                        >
                          Edit
                        </Link>
                        <button
                          onClick={() => handleDelete(item._id)}
                          className="text-red-600 hover:text-red-800 text-sm font-medium"
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </motion.tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

"use client";
import { useState } from "react";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import { authApi } from "@/lib/api";
import { authStorage, AuthUser } from "@/lib/auth";

export default function AdminLogin() {
  const router = useRouter();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const response = await authApi.login(formData.email, formData.password);

      if (response.success && response.data) {
        const userData = response.data as AuthUser;
        authStorage.setUser(userData);
        router.push("/admin/dashboard");
      } else {
        setError(response.error || "Invalid credentials");
      }
    } catch (err: any) {
      setError("Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-diner-cream py-12 px-4">
      <motion.div
        className="bg-white rounded-xl shadow-diner p-8 w-full max-w-md"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <h1 className="text-3xl font-bold text-diner-coffee mb-2 text-center">Admin Login</h1>
        <p className="text-diner-coffee/70 text-center mb-6">Sign in to manage your restaurant</p>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-diner-coffee mb-1">
              Email
            </label>
            <input
              type="email"
              required
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
              placeholder="admin@diner.com"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-diner-coffee mb-1">
              Password
            </label>
            <input
              type="password"
              required
              value={formData.password}
              onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
              placeholder="••••••••"
            />
          </div>

          {error && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg">
              {error}
            </div>
          )}

          <motion.button
            type="submit"
            disabled={loading}
            className="w-full bg-diner-coffee text-diner-cream py-3 rounded-lg font-semibold hover:bg-diner-terracotta transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            whileHover={{ scale: loading ? 1 : 1.02 }}
            whileTap={{ scale: loading ? 1 : 0.98 }}
          >
            {loading ? "Signing in..." : "Sign In"}
          </motion.button>
        </form>
      </motion.div>
    </div>
  );
}

"use client";
import { useState, useEffect } from "react";
import { useRouter, useParams } from "next/navigation";
import { motion } from "framer-motion";
import { menuApi } from "@/lib/api";
import { authStorage } from "@/lib/auth";

const CATEGORIES = [
  "Breakfast",
  "Brunch",
  "Lunch",
  "Dinner",
  "Coffee & Drinks",
  "Cocktails, Beer, Wine",
  "Vegetarian & Healthy Options",
  "Kids Menu",
];

export default function EditMenuItem() {
  const router = useRouter();
  const params = useParams();
  const itemId = params.id as string;
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "Breakfast",
    imageUrl: "",
    isVegetarian: false,
    isGlutenFree: false,
    isKids: false,
    isAvailable: true,
    happyHourPrice: "",
  });

  useEffect(() => {
    const fetchItem = async () => {
      const token = authStorage.getToken();
      if (!token) {
        router.push("/admin/login");
        return;
      }

      try {
        const response = await menuApi.getById(itemId);
        if (response.success && response.data) {
          const item = response.data as any;
          setFormData({
            name: item.name || "",
            description: item.description || "",
            price: item.price?.toString() || "",
            category: item.category || "Breakfast",
            imageUrl: item.imageUrl || "",
            isVegetarian: item.isVegetarian || false,
            isGlutenFree: item.isGlutenFree || false,
            isKids: item.isKids || false,
            isAvailable: item.isAvailable !== false,
            happyHourPrice: item.happyHourPrice?.toString() || "",
          });
        } else {
          setError("Menu item not found");
        }
      } catch (err) {
        setError("Failed to load menu item");
      } finally {
        setLoading(false);
      }
    };

    if (itemId) {
      fetchItem();
    }
  }, [itemId, router]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setSaving(true);

    const token = authStorage.getToken();
    if (!token) {
      router.push("/admin/login");
      return;
    }

    try {
      const menuItemData = {
        ...formData,
        price: parseFloat(formData.price),
        happyHourPrice: formData.happyHourPrice ? parseFloat(formData.happyHourPrice) : undefined,
      };

      const response = await menuApi.update(itemId, menuItemData, token);

      if (response.success) {
        router.push("/admin/dashboard");
      } else {
        setError(response.error || "Failed to update menu item");
      }
    } catch (err: any) {
      setError("An error occurred. Please try again.");
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-diner-cream">
        <p className="text-diner-coffee">Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-diner-cream py-12 px-4">
      <div className="max-w-2xl mx-auto">
        <motion.div
          className="bg-white rounded-xl shadow-diner p-8"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <h1 className="text-3xl font-bold text-diner-coffee mb-6">Edit Menu Item</h1>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-diner-coffee mb-1">
                Name *
              </label>
              <input
                type="text"
                required
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-diner-coffee mb-1">
                Description *
              </label>
              <textarea
                required
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                rows={3}
                className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-diner-coffee mb-1">
                  Price ($) *
                </label>
                <input
                  type="number"
                  step="0.01"
                  min="0"
                  required
                  value={formData.price}
                  onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                  className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-diner-coffee mb-1">
                  Happy Hour Price ($)
                </label>
                <input
                  type="number"
                  step="0.01"
                  min="0"
                  value={formData.happyHourPrice}
                  onChange={(e) => setFormData({ ...formData, happyHourPrice: e.target.value })}
                  className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-diner-coffee mb-1">
                Category *
              </label>
              <select
                required
                value={formData.category}
                onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
              >
                {CATEGORIES.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-diner-coffee mb-1">
                Image URL (Optional)
              </label>
              <input
                type="url"
                value={formData.imageUrl}
                onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                className="w-full px-4 py-2 border border-diner-coffee/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-diner-terracotta"
                placeholder="https://example.com/image.jpg"
              />
            </div>

            <div className="space-y-2">
              <label className="block text-sm font-medium text-diner-coffee mb-1">
                Options
              </label>
              <div className="space-y-2">
                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    checked={formData.isVegetarian}
                    onChange={(e) => setFormData({ ...formData, isVegetarian: e.target.checked })}
                    className="rounded"
                  />
                  <span className="text-diner-coffee">Vegetarian</span>
                </label>
                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    checked={formData.isGlutenFree}
                    onChange={(e) => setFormData({ ...formData, isGlutenFree: e.target.checked })}
                    className="rounded"
                  />
                  <span className="text-diner-coffee">Gluten Free</span>
                </label>
                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    checked={formData.isKids}
                    onChange={(e) => setFormData({ ...formData, isKids: e.target.checked })}
                    className="rounded"
                  />
                  <span className="text-diner-coffee">Kids Menu</span>
                </label>
                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    checked={formData.isAvailable}
                    onChange={(e) => setFormData({ ...formData, isAvailable: e.target.checked })}
                    className="rounded"
                  />
                  <span className="text-diner-coffee">Available</span>
                </label>
              </div>
            </div>

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg">
                {error}
              </div>
            )}

            <div className="flex gap-4 pt-4">
              <button
                type="button"
                onClick={() => router.back()}
                className="flex-1 px-4 py-2 border border-diner-coffee text-diner-coffee rounded-lg hover:bg-diner-cream transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={saving}
                className="flex-1 px-4 py-2 bg-diner-coffee text-diner-cream rounded-lg hover:bg-diner-terracotta transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {saving ? "Saving..." : "Save Changes"}
              </button>
            </div>
          </form>
        </motion.div>
      </div>
    </div>
  );
}

